
public class OperatorenI {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int z1 = 5;
		int z2 = 10;
		int z3 = 5;
		
		int ergebnis;
		
		ergebnis = z1 + z2;
		System.out.println(ergebnis);
		
		ergebnis = z1 - z2;
		System.out.println(ergebnis);

		ergebnis = z1 * z2;
		System.out.println(ergebnis);

		ergebnis = z1 / z2;
		System.out.println(ergebnis);
		
		/*
		 * byte
		 * boolean
		 * char
		 * int
		 * long
		 * float
		 * double
		 * 
		 */
	}

}
