
public class intFloatDouble {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var_zahl_1 = 10 / 3;
		float var_zahl_2 = 10 / 3f;
		double var_zahl_3 = 10 / 3.0;
		
		System.out.println("int " + var_zahl_1);
		System.out.println("float " + var_zahl_2);
		System.out.println("double " + var_zahl_3);
		
		
	}

}
